mapper.select:
SELECT u.* FROM vx_user u LEFT JOIN vx_user_department ud ON ud.user_id = u.id LEFT JOIN vx_department d ON d.id = ud.dept_id
u.deleted = false AND d.status = 1
AND u.id = #{param.id}
AND ( d.id = #{param.departmentId} OR d.ancestors LIKE CONCAT('%', #{param.departmentId}, '%') )
AND u.username LIKE CONCAT('%', #{param.username}, '%')
AND u.nickname LIKE CONCAT('%', #{param.nickname}, '%')
AND u.gender = #{gender}
AND u.email LIKE CONCAT('%', #{param.email}, '%')
AND u.status = #{param.status}
AND ( u.username LIKE CONCAT('%', #{param.keyword}, '%') OR u.nickname LIKE CONCAT('%', #{param.keyword}, '%') OR u.description LIKE CONCAT('%', #{param.keyword}, '%') )
AND u.create_time >= #{param.startTime}
AND u.create_time <= #{param.endTime}
